import React, { useState } from 'react'

import {
    Container,
    TitleProgess,
    Title,
    ContainerBody,
    Ajusted,
    ContainerButton,
    ContainerAlignRow,
    ContainerPicker,
    TitlePicker,
    ButtonOpenPicker,
    ContainerCheckStudent,
    TextConfirmStudent,
    ButtonCheck
} from './styles'

import { Button } from '../../../components/Button'
import { Controlled } from '../../../components/Form/Controlled'
import { StackParamsList } from '../../../routes/auth.routes'
import { schemaDoctor } from '../../../utils/validationForm'

import { useForm } from 'react-hook-form'
import { useNavigation, useRoute, RouteProp } from '@react-navigation/native'
import { AntDesign, Ionicons } from '@expo/vector-icons';
import { NativeStackNavigationProp } from '@react-navigation/native-stack'
import { yupResolver } from '@hookform/resolvers/yup'

type FormDataDoctor = {
    crm: string;
    estadocrm: string;
    dataFormatura: string;
    universidade: string;
    estado: string;
}
type RouteDetailParams = {
    Doctor: {
        name: string;
        cpf: string;
        dateNasc: string;
    }
}

type DoctorRouteProp = RouteProp<RouteDetailParams, "Doctor">

export function Doctor() {

    const navigation = useNavigation<NativeStackNavigationProp<StackParamsList>>()
    const route = useRoute<DoctorRouteProp>();
    const [pressed, setPressed] = useState(true);
    //descontrindo os valores recebidos pelo route.params
    const my = route.params
    const { control, handleSubmit } = useForm<FormDataDoctor>()

    async function handle(doctor: FormDataDoctor) {
        const _OBJECT = {
            my: my,
            doctor: doctor
        }
        const _doctor = Object.assign({}, _OBJECT.my, _OBJECT.doctor)
            navigation.navigate('Acess', _doctor)
    }

    return (
        <Container>

            <TitleProgess>PASSO 3 DE 5</TitleProgess>

            <Title>Dados do médico</Title>

            <ContainerBody>

                <ContainerCheckStudent>
                    <ButtonCheck onPress={() => setPressed(!pressed)}>
                        {pressed ? (
                            <Ionicons name="checkmark-circle-outline" size={24} color="white" />
                        ) : (
                            <Ionicons name="checkmark-circle" size={24} color="white" />
                        )}
                    </ButtonCheck>
                    <TextConfirmStudent>Sou estudante</TextConfirmStudent>
                </ContainerCheckStudent>

                {pressed ? (
                    <>
                        <ContainerAlignRow>
                            <Ajusted style={{ marginRight: 10 }}>
                                <Controlled
                                    name='crm'
                                    control={control}
                                    placeholder='CRM' placeholderTextColor="#fff"
                                />
                            </Ajusted>
                            <Ajusted style={{ marginLeft: 10 }}>
                                <Controlled
                                    name='estadocrm'
                                    control={control}
                                    placeholder='Estado do CRM' placeholderTextColor="#fff"
                                />
                            </Ajusted>
                        </ContainerAlignRow>

                        <Controlled
                            name='dataFormatura'
                            control={control}
                            placeholder='Data da formatura' placeholderTextColor="#fff"
                            type="date"
                        />

                        <ContainerPicker>
                            <TitlePicker>Especialidades</TitlePicker>
                            <ButtonOpenPicker onPress={() => alert('em desenvolvimento...')}>
                                <AntDesign name="down" size={24} color="white" />
                            </ButtonOpenPicker>
                        </ContainerPicker>
                    </>
                ) : (
                    <>
                        <Controlled
                            type="form"
                            name='universidade'
                            control={control}
                            placeholder="Sua universidade"
                            placeholderTextColor="#fff"
                        />
                        <Controlled
                            name='estado'
                            control={control}
                            placeholder="Seu Estado"
                            placeholderTextColor="#fff"
                        />
                    </>
                )}
                <ContainerButton>
                    <Button
                        type="login"
                        onPress={handleSubmit(handle)}
                        title='Próximo' />
                </ContainerButton>

            </ContainerBody>
        </Container>
    )
}
